import { useIntersectionObserver } from '@vueuse/core';
import type { Directive, App } from 'vue';

const defaultImg =
  'data:image/png;base64,';
const imgLazyDirective: Directive = {
  mounted(el, binding) {
    // 懒加载的逻辑：监听当前的img dom对象是否进入到视口区域  如果进入 就把图片的地址交给img 的src属性
    // 一旦src有了值就会自动发送http请求 获取图片资源
    const { stop } = useIntersectionObserver(
      el,
      ([{ isIntersecting }]) => {
        // console.log(observerElement);
        // isIntersecting: 当前监听的元素是否成功进入到视口区域如果进来了就是true 否则就是false
        // 有了一个判断是否进入视口区域的条件 如果为true就发送请求
        if (isIntersecting) {
          // 错误处理
          el.onerror = function () {
            // 使用默认图片交给src
            el.src = defaultImg;
          };
          el.src = binding.value;
          stop();
        }
        // 监听处理不是只进行一次的 每次进入移出视口都会再次执行回调
      },
      // 0 - 1 数值越大代表 要求进入的面积越大才能触发回调函数
      { threshold: 0 },
    );
  },
};
export function setImgLazyDirective(app: App) {
  app.directive('img-lazy', imgLazyDirective);
}
export default imgLazyDirective;
